    function weight($input) {
        $weight = null;
        $parts = explode(',', $input);
        if (isset($parts[0])) {
            $weight = trim($parts[0]);
        }
        return $weight;
    }
    function height($input) {
        $height = null;
        $feet = null;
        $inches = null;
        $parts = explode(',', $input);
        if (isset($parts[1])) {
            $height = trim($parts[1]);
        }
        if ($height !== null) {
            preg_match('/(\d+)\s*FT\s*(\d*)\s*IN*/i', $height, $matches);
            if (isset($matches[1])) {
                $feet = $matches[1];
            }
            if (isset($matches[2])) {
                $inches = $matches[2];
            }
        }
        return $height;
    }
